const payload = Buffer(msg.payload.payload, 'Base64');
let data_type = (payload[0] & 0b11000000) >> 6;
let recovery = (payload[0] & 0b00100000) >> 5;
let recovery_nb = (payload[0] & 0b00011111);

function readTwoBytes(index) {
    return (payload[index] << 8) + payload[index+1];
}

let data = {
    temperature : 0,
    humidity : 0,
    c_pms_1_0 : 'N/A',
    c_pms_2_5 : 'N/A',
    c_pms_10 : 'N/A',
    q_pms_0_3 : 'N/A',
    q_pms_0_5 : 'N/A',
    q_pms_1_0 : 'N/A',
    q_pms_2_5 : 'N/A',
    q_pms_5_0 : 'N/A',
    q_pms_10 : 'N/A'
};

if(data_type === 0) {
    data = 'Err';
} else {
    data.temperature = payload[1]/2;
    data.humidity = payload[2];
    if(data_type === 1 || data_type === 3) {
        data.c_pms_1_0 = readTwoBytes(3);
        data.c_pms_2_5 = readTwoBytes(5);
        data.c_pms_10 = readTwoBytes(7);
    }
    if(data_type === 2 || data_type === 3) {
        let shift = 0;
        if(data_type === 3) {
            shift = 6
        }
        data.q_pms_0_3 = readTwoBytes(3+shift);
        data.q_pms_0_5 = readTwoBytes(5+shift);
        data.q_pms_1_0 = readTwoBytes(7+shift);
        data.q_pms_2_5 = readTwoBytes(9+shift);
        data.q_pms_5_0 = readTwoBytes(11+shift);
        data.q_pms_10 = readTwoBytes(13+shift);
    }
}
msg.payload.payload = {
    data_type : data_type,
    recovery : recovery === 1,
    recovery_nb : recovery_nb*recovery,
    data : data
};

return msg;

